"""SSH ê²½ìœ  Docker CLI ë˜í¼"""

import json

import streamlit as st

from config import PROJECT_DIR, COMPOSE_FILE_DEV
from utils.ssh import run_cmd


@st.cache_data(ttl=10)
def list_containers(host: str) -> list[dict]:
    """docker ps -a â†’ ì»¨í…Œì´ë„ˆ ë¦¬ìŠ¤íŠ¸ ë°˜í™˜ (10ì´ˆ ìºì‹œ)"""
    fmt = '{"name":"{{.Names}}","image":"{{.Image}}","status":"{{.Status}}","ports":"{{.Ports}}","state":"{{.State}}"}'
    result = run_cmd(host, f'docker ps -a --format \'{fmt}\'')
    if result.exit_code != 0:
        return []

    containers = []
    for line in result.stdout.strip().splitlines():
        if not line.strip():
            continue
        try:
            containers.append(json.loads(line))
        except json.JSONDecodeError:
            continue
    return containers


def container_action(host: str, container: str, action: str) -> tuple[bool, str]:
    """ì»¨í…Œì´ë„ˆ start/stop/restart"""
    if action not in ("start", "stop", "restart"):
        return False, f"ì§€ì›í•˜ì§€ ì•ŠëŠ” ì•¡ì…˜: {action}"
    result = run_cmd(host, f'docker {action} "{container}"', timeout=60)
    if result.exit_code == 0:
        list_containers.clear()
        return True, f"{container} {action} ì™„ë£Œ"
    return False, result.stderr or result.stdout


def container_logs(host: str, container: str, lines: int = 100) -> str:
    """ì»¨í…Œì´ë„ˆ ìµœê·¼ ë¡œê·¸"""
    result = run_cmd(host, f'docker logs --tail {lines} "{container}" 2>&1', timeout=15)
    return result.stdout if result.exit_code == 0 else f"ë¡œê·¸ ì¡°íšŒ ì‹¤íŒ¨: {result.stderr}"


def compose_up(
    host: str, compose_file: str = COMPOSE_FILE_DEV, project_dir: str = PROJECT_DIR
) -> tuple[bool, str]:
    """docker compose up -d"""
    cmd = f'cd {project_dir} && docker compose -f "{compose_file}" up -d 2>&1'
    result = run_cmd(host, cmd, timeout=120)
    if result.exit_code == 0:
        list_containers.clear()
    return result.exit_code == 0, result.stdout + result.stderr


def compose_down(
    host: str, compose_file: str = COMPOSE_FILE_DEV, project_dir: str = PROJECT_DIR
) -> tuple[bool, str]:
    """docker compose down"""
    cmd = f'cd {project_dir} && docker compose -f "{compose_file}" down 2>&1'
    result = run_cmd(host, cmd, timeout=60)
    if result.exit_code == 0:
        list_containers.clear()
    return result.exit_code == 0, result.stdout + result.stderr


def compose_pull(
    host: str, compose_file: str = COMPOSE_FILE_DEV, project_dir: str = PROJECT_DIR
) -> tuple[bool, str]:
    """docker compose pull â†’ up -d"""
    cmd = (
        f'cd {project_dir} && docker compose -f "{compose_file}" pull 2>&1'
        f' && docker compose -f "{compose_file}" up -d 2>&1'
    )
    result = run_cmd(host, cmd, timeout=180)
    if result.exit_code == 0:
        list_containers.clear()
    return result.exit_code == 0, result.stdout + result.stderr


@st.cache_data(ttl=10)
def docker_stats(host: str) -> list[dict]:
    """docker stats --no-stream â†’ CPU/ë©”ëª¨ë¦¬ ì‚¬ìš©ëŸ‰ (10ì´ˆ ìºì‹œ)"""
    fmt = '{"name":"{{.Name}}","cpu":"{{.CPUPerc}}","mem":"{{.MemUsage}}","mem_perc":"{{.MemPerc}}","net":"{{.NetIO}}"}'
    result = run_cmd(host, f'docker stats --no-stream --format \'{fmt}\'', timeout=15)
    if result.exit_code != 0:
        return []

    stats = []
    for line in result.stdout.strip().splitlines():
        if not line.strip():
            continue
        try:
            stats.append(json.loads(line))
        except json.JSONDecodeError:
            continue
    return stats

"""Paramiko SSH ì—°ê²° í’€ + ëª…ë ¹ ì‹¤í–‰ ìœ í‹¸ë¦¬í‹°"""

import threading
from dataclasses import dataclass

import paramiko

from config import SSH_USER, SSH_KEY_PATH


@dataclass
class CommandResult:
    stdout: str
    stderr: str
    exit_code: int


class SSHManager:
    """ì„œë²„ë³„ SSH ì—°ê²°ì„ ê´€ë¦¬í•˜ëŠ” ì‹±ê¸€í„´ ë§¤ë‹ˆì €"""

    _instance = None
    _lock = threading.Lock()

    def __new__(cls):
        if cls._instance is None:
            with cls._lock:
                if cls._instance is None:
                    cls._instance = super().__new__(cls)
                    cls._instance._connections: dict[str, paramiko.SSHClient] = {}
                    cls._instance._conn_lock = threading.Lock()
        return cls._instance

    def _create_client(self, host: str) -> paramiko.SSHClient:
        """ìƒˆ SSH í´ë¼ì´ì–¸íŠ¸ ìƒì„±"""
        client = paramiko.SSHClient()
        client.set_missing_host_key_policy(paramiko.AutoAddPolicy())
        try:
            pkey = paramiko.Ed25519Key.from_private_key_file(SSH_KEY_PATH)
            client.connect(
                hostname=host,
                username=SSH_USER,
                pkey=pkey,
                timeout=10,
                banner_timeout=10,
            )
        except Exception as e:
            raise ConnectionError(f"SSH ì—°ê²° ì‹¤íŒ¨ ({host}): {e}")
        return client

    def get_connection(self, host: str) -> paramiko.SSHClient:
        """ê¸°ì¡´ ì—°ê²° ì¬ì‚¬ìš© ë˜ëŠ” ìƒˆ ì—°ê²° ìƒì„±"""
        with self._conn_lock:
            client = self._connections.get(host)
            if client is not None:
                # ì—°ê²° ìœ íš¨ì„± ê²€ì‚¬
                transport = client.get_transport()
                if transport is not None and transport.is_active():
                    return client
                # ì—°ê²° ëŠê¹€ â€” ì¬ìƒì„±
                try:
                    client.close()
                except Exception:
                    pass

            client = self._create_client(host)
            self._connections[host] = client
            return client

    def run_command(self, host: str, cmd: str, timeout: int = 30) -> CommandResult:
        """SSHë¡œ ëª…ë ¹ ì‹¤í–‰ í›„ ê²°ê³¼ ë°˜í™˜"""
        try:
            client = self.get_connection(host)
            _, stdout_ch, stderr_ch = client.exec_command(cmd, timeout=timeout)
            exit_code = stdout_ch.channel.recv_exit_status()
            stdout = stdout_ch.read().decode("utf-8", errors="replace")
            stderr = stderr_ch.read().decode("utf-8", errors="replace")
            return CommandResult(stdout=stdout, stderr=stderr, exit_code=exit_code)
        except ConnectionError:
            raise
        except Exception as e:
            # ì—°ê²°ì´ ëŠì–´ì¡Œì„ ìˆ˜ ìˆìœ¼ë¯€ë¡œ ìºì‹œì—ì„œ ì œê±°
            with self._conn_lock:
                self._connections.pop(host, None)
            return CommandResult(stdout="", stderr=f"ëª…ë ¹ ì‹¤í–‰ ì‹¤íŒ¨: {e}", exit_code=-1)

    def check_server_alive(self, host: str) -> bool:
        """ì„œë²„ SSH ì ‘ì† ê°€ëŠ¥ ì—¬ë¶€ í™•ì¸"""
        try:
            result = self.run_command(host, "echo ok", timeout=5)
            return result.exit_code == 0 and "ok" in result.stdout
        except Exception:
            return False

    def close_all(self):
        """ëª¨ë“  ì—°ê²° ì¢…ë£Œ"""
        with self._conn_lock:
            for client in self._connections.values():
                try:
                    client.close()
                except Exception:
                    pass
            self._connections.clear()


# ì „ì—­ ì¸ìŠ¤í„´ìŠ¤
ssh_manager = SSHManager()


def run_cmd(host: str, cmd: str, timeout: int = 30) -> CommandResult:
    """í¸ì˜ í•¨ìˆ˜: SSH ëª…ë ¹ ì‹¤í–‰"""
    return ssh_manager.run_command(host, cmd, timeout)


def is_server_online(host: str) -> bool:
    """í¸ì˜ í•¨ìˆ˜: ì„œë²„ ì˜¨ë¼ì¸ ì—¬ë¶€"""
    return ssh_manager.check_server_alive(host)

