     1→/**
     2→ * Landing 페이지 모바일 반응형 E2E 테스트
     3→ * Test IDs: FE-LAND-M01 ~ FE-LAND-M05
     4→ * 대상 기기: 모바일 9종 (playwright.config.js projects 참조)
     5→ */
     6→import { test, expect } from '@playwright/test';
     7→
     8→test.describe('FE-LAND-M: Landing 모바일 반응형', () => {
     9→  test.beforeEach(async ({ page }) => {
    10→    // 미인증 상태로 랜딩 접근
    11→    await page.goto('/');
    12→  });
    13→
    14→  /**
    15→   * FE-LAND-M01: ADELIE 브랜드 텍스트가 viewport 내에 표시되는지 확인
    16→   * 검증: clamp(3rem,20vw,5.5rem) 적용 후 텍스트 잘림 없음
    17→   */
    18→  test('FE-LAND-M01: ADELIE 텍스트가 viewport 안에 완전히 표시됨', async ({ page, viewport }) => {
    19→    // Hero 슬라이드 대기
    20→    const brandText = page.locator('p.font-black', { hasText: 'ADELIE' }).first();
    21→    await expect(brandText).toBeVisible();
    22→
    23→    const box = await brandText.boundingBox();
    24→    expect(box).not.toBeNull();
    25→
    26→    // 텍스트가 viewport 너비를 벗어나지 않음
    27→    expect(box.x).toBeGreaterThanOrEqual(0);
    28→    expect(box.x + box.width).toBeLessThanOrEqual(viewport.width + 1); // 1px 허용오차
    29→
    30→    // clamp 하한(3rem=48px) 이상 상한(5.5rem=88px) 이하 높이 확인
    31→    expect(box.height).toBeGreaterThanOrEqual(40); // 3rem 기준 최소
    32→    expect(box.height).toBeLessThanOrEqual(100);   // 5.5rem + 여유
    33→  });
    34→
    35→  /**
    36→   * FE-LAND-M02: Hero 이미지가 콘텐츠를 덮어 텍스트를 가리지 않는지 확인
    37→   * 검증: clamp bottom 적용으로 이미지가 텍스트 영역 침범 방지
    38→   */
    39→  test('FE-LAND-M02: Hero 이미지가 텍스트 영역을 가리지 않음', async ({ page }) => {
    40→    const brandText = page.locator('p.font-black', { hasText: 'ADELIE' }).first();
    41→    const heroImage = page.locator('img[alt="ADELIE 랜딩 메인 비주얼"]').first();
    42→
    43→    await expect(brandText).toBeVisible();
    44→
    45→    // 이미지가 존재하는 경우 텍스트와 겹치지 않음
    46→    const imageCount = await heroImage.count();
    47→    if (imageCount > 0) {
    48→      const textBox = await brandText.boundingBox();
    49→      const imageBox = await heroImage.boundingBox();
    50→
    51→      if (textBox && imageBox) {
    52→        // 이미지 상단이 텍스트 하단보다 아래에 있거나
    53→        // z-index로 분리되어 있어 텍스트가 클릭 가능해야 함
    54→        const textIsClickable = await brandText.isVisible();
    55→        expect(textIsClickable).toBe(true);
    56→      }
    57→    }
    58→  });
    59→
    60→  /**
    61→   * FE-LAND-M03: 건너뛰기 버튼이 표시되고 동작하는지 확인
    62→   * 검증: 터치 영역 40px 이상, viewport 내 위치
    63→   */
    64→  test('FE-LAND-M03: 건너뛰기 버튼 표시 및 터치 영역 확인', async ({ page, viewport }) => {
    65→    const skipBtn = page.locator('button[aria-label="랜딩 건너뛰기"]');
    66→    await expect(skipBtn).toBeVisible();
    67→
    68→    const box = await skipBtn.boundingBox();
    69→    expect(box).not.toBeNull();
    70→
    71→    // viewport 내에 위치
    72→    expect(box.x).toBeGreaterThanOrEqual(0);
    73→    expect(box.x + box.width).toBeLessThanOrEqual(viewport.width + 1);
    74→    expect(box.y).toBeGreaterThanOrEqual(0);
    75→  });
    76→
    77→  /**
    78→   * FE-LAND-M04: Hero에서 Feature 슬라이드로 자동 전환 확인 (3초 후)
    79→   * 검증: 3초 대기 후 feature 슬라이드 요소 출현
    80→   */
    81→  test('FE-LAND-M04: 3초 후 Feature 슬라이드로 자동 전환', async ({ page }) => {
    82→    // Hero 슬라이드 확인
    83→    await expect(page.locator('p.font-black', { hasText: 'ADELIE' })).toBeVisible();
    84→
    85→    // 3.5초 대기 (자동 전환 3000ms)
    86→    await page.waitForTimeout(3500);
    87→
    88→    // Feature 슬라이드 버튼(dot)이 나타나야 함
    89→    const dots = page.locator('button[aria-label*="번째 랜딩 화면으로 이동"]');
    90→    await expect(dots.first()).toBeVisible({ timeout: 2000 });
    91→  });
    92→
    93→  /**
    94→   * FE-LAND-M05: Feature 슬라이드 이미지가 viewport 너비를 벗어나지 않음
    95→   * 검증: max-w-[80vw] 적용으로 이미지 잘림 방지
    96→   */
    97→  test('FE-LAND-M05: Feature 이미지가 viewport 내에 표시됨', async ({ page, viewport }) => {
    98→    // 자동 전환 대기
    99→    await page.waitForTimeout(3500);
   100→
   101→    const featureImages = page.locator('img[alt*="비주얼"]');
   102→    const count = await featureImages.count();
   103→
   104→    for (let i = 0; i < count; i++) {
   105→      const img = featureImages.nth(i);
   106→      if (await img.isVisible()) {
   107→        const box = await img.boundingBox();
   108→        if (box) {
   109→          // 이미지가 viewport 너비 내에 있음
   110→          expect(box.x + box.width).toBeLessThanOrEqual(viewport.width + 2);
   111→        }
   112→      }
   113→    }
   114→  });
   115→});
   116→

