     1→/**
     2→ * Agent Canvas 모바일 반응형 E2E 테스트
     3→ * Test IDs: FE-AGENT-M01 ~ FE-AGENT-M04
     4→ * 대상 컴포넌트: AgentDock, AgentCanvasPage, AgentHistoryPage
     5→ */
     6→import { test, expect } from '@playwright/test';
     7→
     8→test.describe('FE-AGENT-M: Agent Canvas 모바일 반응형', () => {
     9→  test.beforeEach(async ({ page }) => {
    10→    await page.goto('/home');
    11→    // 페이지 로드 대기
    12→    await page.waitForLoadState('networkidle').catch(() => {});
    13→  });
    14→
    15→  /**
    16→   * FE-AGENT-M01: AgentDock 입력바가 viewport 내에 완전히 표시됨
    17→   * 검증: 입력바가 viewport 너비를 벗어나지 않고 bottom nav 위에 위치
    18→   */
    19→  test('FE-AGENT-M01: AgentDock 입력바가 viewport 내에 표시됨', async ({ page, viewport }) => {
    20→    // AgentDock은 fixed bottom에 표시
    21→    const agentInput = page.locator('input[aria-label="에이전트 질문 입력"]').first();
    22→    const dockExists = await agentInput.isVisible().catch(() => false);
    23→
    24→    if (!dockExists) {
    25→      // AgentDock이 현재 페이지에 숨겨진 경우 스킵
    26→      test.skip();
    27→      return;
    28→    }
    29→
    30→    const form = agentInput.locator('xpath=ancestor::form');
    31→    const dockWrapper = form.locator('xpath=ancestor::div[contains(@class,"fixed")]');
    32→    const box = await dockWrapper.boundingBox().catch(() => null);
    33→
    34→    if (box) {
    35→      // dock이 viewport 너비를 벗어나지 않음
    36→      expect(box.x).toBeGreaterThanOrEqual(0);
    37→      expect(box.x + box.width).toBeLessThanOrEqual(viewport.width + 1);
    38→      // dock이 viewport 하단 근처에 위치
    39→      expect(box.y + box.height).toBeLessThanOrEqual(viewport.height + 5);
    40→    }
    41→  });
    42→
    43→  /**
    44→   * FE-AGENT-M02: AgentDock 아이콘 버튼들이 가로 overflow 없음
    45→   * 검증: 4개 아이콘 버튼(h-7 w-7)이 입력바 안에서 잘리지 않음
    46→   */
    47→  test('FE-AGENT-M02: AgentDock 아이콘 버튼 overflow 없음', async ({ page, viewport }) => {
    48→    const submitBtn = page.locator('button[aria-label="질문 전송"]').first();
    49→    const submitExists = await submitBtn.isVisible().catch(() => false);
    50→
    51→    if (!submitExists) {
    52→      test.skip();
    53→      return;
    54→    }
    55→
    56→    const submitBox = await submitBtn.boundingBox();
    57→    if (submitBox) {
    58→      // 전송 버튼(가장 오른쪽)이 viewport를 벗어나지 않음
    59→      expect(submitBox.x + submitBox.width).toBeLessThanOrEqual(viewport.width + 2);
    60→      // 버튼 크기가 28px(h-7 w-7) 이상
    61→      expect(submitBox.width).toBeGreaterThanOrEqual(24);
    62→      expect(submitBox.height).toBeGreaterThanOrEqual(24);
    63→    }
    64→
    65→    // 스파클 버튼(추천 문구)도 확인
    66→    const sparkleBtn = page.locator('button[aria-label="추천 문구 사용"]').first();
    67→    const sparkleExists = await sparkleBtn.isVisible().catch(() => false);
    68→    if (sparkleExists) {
    69→      const sparkleBox = await sparkleBtn.boundingBox();
    70→      if (sparkleBox) {
    71→        expect(sparkleBox.x).toBeGreaterThanOrEqual(0);
    72→      }
    73→    }
    74→  });
    75→
    76→  /**
    77→   * FE-AGENT-M03: AgentHistoryPage 세션 목록의 삭제 버튼 접근 가능
    78→   * 검증: 삭제 버튼이 세션 카드 우측에서 접근 가능하며 잘리지 않음
    79→   */
    80→  test('FE-AGENT-M03: AgentHistory 삭제 버튼 접근성', async ({ page, viewport }) => {
    81→    await page.goto('/agent/history');
    82→    await page.waitForLoadState('networkidle').catch(() => {});
    83→
    84→    // 세션 목록이 있는 경우 삭제 버튼 확인
    85→    const deleteBtn = page.locator('button', { hasText: '삭제' }).first();
    86→    const deleteExists = await deleteBtn.isVisible().catch(() => false);
    87→
    88→    if (deleteExists) {
    89→      const box = await deleteBtn.boundingBox();
    90→      if (box) {
    91→        // 삭제 버튼이 viewport 내에 있음
    92→        expect(box.x + box.width).toBeLessThanOrEqual(viewport.width + 2);
    93→        // 버튼이 화면 오른쪽에서 잘리지 않음 (최소 16px 여백)
    94→        expect(viewport.width - (box.x + box.width)).toBeGreaterThanOrEqual(-2);
    95→      }
    96→    } else {
    97→      // 세션이 없는 경우 — 빈 상태 메시지 확인
    98→      const emptyMsg = page.locator('text=저장된 대화가 없습니다');
    99→      const emptyExists = await emptyMsg.isVisible().catch(() => false);
   100→      // 빈 상태이거나 세션이 있어야 함 (둘 다 허용)
   101→      expect(emptyExists || !deleteExists).toBe(true);
   102→    }
   103→  });
   104→
   105→  /**
   106→   * FE-AGENT-M04: AgentDock 세션 복귀 레이블이 truncate 처리됨
   107→   * 검증: "진행 중인 대화가 있어요" 텍스트가 max-w-[8rem]으로 잘림 처리
   108→   */
   109→  test('FE-AGENT-M04: AgentDock 세션 복귀 레이블 overflow 처리', async ({ page, viewport }) => {
   110→    const resumeLabel = page.locator('.truncate.max-w-\\[8rem\\]', { hasText: '진행 중인 대화가 있어요' }).first();
   111→    const labelExists = await resumeLabel.isVisible().catch(() => false);
   112→
   113→    if (labelExists) {
   114→      const box = await resumeLabel.boundingBox();
   115→      if (box) {
   116→        // 레이블이 최대 8rem(128px) 이내
   117→        expect(box.width).toBeLessThanOrEqual(130);
   118→        // viewport 내에 위치
   119→        expect(box.x + box.width).toBeLessThanOrEqual(viewport.width + 2);
   120→      }
   121→    }
   122→    // 레이블이 없는 경우(세션 없음) — 테스트 패스
   123→  });
   124→});
