     5→import { lazy, Suspense } from 'react';
     6→import { BrowserRouter, Routes, Route, Navigate, useParams } from 'react-router-dom';
     7→import { UserProvider, useUser } from './contexts/UserContext';
     8→import { PortfolioProvider } from './contexts/PortfolioContext';
     9→import { TutorProvider } from './contexts/TutorContext';
    10→import { TermProvider } from './contexts/TermContext';
    11→import { ThemeProvider } from './contexts/ThemeContext';
    12→import { TutorModal, ErrorBoundary, ToastProvider } from './components';
