    16→import PenguinLoading from './components/common/PenguinLoading';
    17→import UpdatePrompt from './components/common/UpdatePrompt';
    18→
    19→// 코드 스플리팅: 각 페이지를 동적 import
    20→const Auth = lazy(() => import('./pages/Auth'));
    21→const Landing = lazy(() => import('./pages/Landing'));
    22→const Home = lazy(() => import('./pages/Home'));
    23→const Education = lazy(() => import('./pages/Education'));
    24→const ActivityArchive = lazy(() => import('./pages/ActivityArchive'));
    25→const Search = lazy(() => import('./pages/Search'));
    26→const Comparison = lazy(() => import('./pages/Comparison'));
    27→const Story = lazy(() => import('./pages/Story'));
    28→const Companies = lazy(() => import('./pages/Companies'));
    29→const History = lazy(() => import('./pages/History'));
    30→const Narrative = lazy(() => import('./pages/Narrative'));
    31→const Notifications = lazy(() => import('./pages/Notifications'));
    32→const Portfolio = lazy(() => import('./pages/Portfolio'));
    33→const Profile = lazy(() => import('./pages/Profile'));
    34→const TutorChat = lazy(() => import('./pages/TutorChat'));
    35→const AgentCanvasPage = lazy(() => import('./pages/AgentCanvasPage'));
    36→const AgentHistoryPage = lazy(() => import('./pages/AgentHistoryPage'));
    37→
    38→function CaseRedirect() {
    39→  const { caseId } = useParams();
    40→  if (!caseId) return <Navigate to="/search" replace />;
    41→  return <Navigate to={`/narrative/${caseId}`} replace />;
    42→}
    43→
    44→function ProtectedRoute({ children }) {
    45→  const { user, isLoading } = useUser();
    46→  if (isLoading) return <PageLoader />;
    47→  if (!user?.isAuthenticated) return <Navigate to="/" replace />;
    48→  return children;
    49→}
    50→
    51→function PageLoader() {
    52→  return (
    53→    <div className="min-h-screen bg-background flex items-center justify-center">
    54→      <PenguinLoading size="sm" />
    55→    </div>
    56→  );
    57→}
    58→
    59→function AppRoutes() {
    60→  return (
    61→    <Suspense fallback={<PageLoader />}>
    62→      <Routes>
    63→        <Route path="/" element={<Landing />} />
    64→        <Route path="/landing" element={<Navigate to="/" replace />} />
    65→        <Route path="/onboarding" element={<Navigate to="/" replace />} />
    66→        <Route path="/auth" element={<Auth />} />
    67→        <Route path="/education" element={<ProtectedRoute><Education /></ProtectedRoute>} />
    68→        <Route path="/education/archive" element={<ProtectedRoute><ActivityArchive /></ProtectedRoute>} />
    69→        <Route path="/home" element={<ProtectedRoute><Home /></ProtectedRoute>} />
    70→        <Route path="/search" element={<ProtectedRoute><Search /></ProtectedRoute>} />
    71→        <Route path="/comparison" element={<ProtectedRoute><Comparison /></ProtectedRoute>} />
    72→        <Route path="/story" element={<ProtectedRoute><Story /></ProtectedRoute>} />
    73→        <Route path="/companies" element={<ProtectedRoute><Companies /></ProtectedRoute>} />
    74→        <Route path="/history" element={<ProtectedRoute><History /></ProtectedRoute>} />
    75→        <Route path="/case/:caseId" element={<ProtectedRoute><CaseRedirect /></ProtectedRoute>} />
    76→        <Route path="/narrative/:caseId" element={<ProtectedRoute><Narrative /></ProtectedRoute>} />
    77→        <Route path="/notifications" element={<ProtectedRoute><Notifications /></ProtectedRoute>} />
    78→        <Route path="/portfolio" element={<ProtectedRoute><Portfolio /></ProtectedRoute>} />
    79→        <Route path="/profile" element={<ProtectedRoute><Profile /></ProtectedRoute>} />
    80→        <Route path="/tutor" element={<ProtectedRoute><TutorChat /></ProtectedRoute>} />
    81→        <Route path="/agent" element={<ProtectedRoute><AgentCanvasPage /></ProtectedRoute>} />
    82→        <Route path="/agent/history" element={<ProtectedRoute><AgentHistoryPage /></ProtectedRoute>} />
    83→        <Route path="*" element={<Navigate to="/" replace />} />
    84→      </Routes>
    85→    </Suspense>
    86→  );
    87→}
    88→
    89→export default function App() {
    90→  return (
    91→    <BrowserRouter>
    92→      <ThemeProvider>
    93→        <UserProvider>
    94→          <PortfolioProvider>
    95→          <TutorProvider>
    96→            <TermProvider>
    97→              <ErrorBoundary>
    98→                <ToastProvider>
    99→                  <div className="app-container">
   100→                    <UpdatePrompt />
   101→                    <AppRoutes />
   102→                    <TermBottomSheet />
   103→                    <TutorModal />
   104→                    <AgentDock />
   105→                    <BottomNav />
   106→                  </div>
   107→                </ToastProvider>
   108→              </ErrorBoundary>
   109→            </TermProvider>
   110→          </TutorProvider>
   111→          </PortfolioProvider>
   112→        </UserProvider>
   113→      </ThemeProvider>
   114→    </BrowserRouter>
   115→  );
   116→}
