import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useUser, DIFFICULTY_LEVELS } from '../contexts';
import { motion } from 'framer-motion';
import AppHeader from '../components/layout/AppHeader';

/* â”€â”€ ë¬¸ì˜ì‚¬í•­ ì»´í¬ë„ŒíŠ¸ â”€â”€ */
function ContactSection() {
  const [message, setMessage] = useState('');
  const [submitted, setSubmitted] = useState(false);
  const [submitting, setSubmitting] = useState(false);

  const handleSubmit = async () => {
    if (!message.trim()) return;
    setSubmitting(true);
    try {
      await fetch('/api/v1/feedback', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ page: 'contact', rating: null, comment: message }),
      });
      setSubmitted(true);
    } catch {}
    setSubmitting(false);
  };

  if (submitted) {
    return (
      <div className="text-center py-4">
        <img src="/images/penguin-3d.png" alt="Adelie" className="w-10 h-10 mx-auto mb-1" />
        <p className="text-sm font-medium">ë¬¸ì˜ê°€ ì ‘ìˆ˜ë˜ì—ˆìŠµë‹ˆë‹¤!</p>
        <p className="text-xs text-text-secondary mt-1">ë¹ ë¥¸ ì‹œì¼ ë‚´ì— ë‹µë³€ë“œë¦¬ê² ìŠµë‹ˆë‹¤.</p>
      </div>
    );
  }

  return (
    <>
      <h2 className="text-lg font-bold mb-3">ë¬¸ì˜ì‚¬í•­</h2>
      <p className="text-sm text-text-secondary mb-4">
        ì„œë¹„ìŠ¤ ì´ìš© ì¤‘ ê¶ê¸ˆí•œ ì ì´ë‚˜ ê°œì„  ì‚¬í•­ì´ ìˆìœ¼ì‹œë©´ ì•„ë˜ì— ë‚¨ê²¨ì£¼ì„¸ìš”.
      </p>
      <textarea
        id="inquiry-message"
        name="message"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        placeholder="ë¬¸ì˜ ë‚´ìš©ì„ ì…ë ¥í•´ì£¼ì„¸ìš”"
        aria-label="ë¬¸ì˜ ë‚´ìš©"
        className="w-full p-3 rounded-xl border border-border bg-surface text-sm resize-none mb-3"
        rows={3}
      />
      <button
        onClick={handleSubmit}
        disabled={!message.trim() || submitting}
        className="w-full py-2.5 rounded-xl font-semibold text-white bg-primary hover:bg-primary/90 disabled:opacity-40 transition-colors text-sm"
      >
        {submitting ? 'ì „ì†¡ ì¤‘...' : 'ë³´ë‚´ê¸°'}
      </button>
    </>
  );
}

const DIFFICULTY_OPTIONS = [
  { value: DIFFICULTY_LEVELS.BEGINNER, label: 'ì…ë¬¸' },
  { value: DIFFICULTY_LEVELS.ELEMENTARY, label: 'ì´ˆê¸‰' },
  { value: DIFFICULTY_LEVELS.INTERMEDIATE, label: 'ì¤‘ê¸‰' },
];

export default function Profile() {
  const navigate = useNavigate();
  const { user, settings, setDifficulty, logout } = useUser();

  return (
    <div className="min-h-screen bg-background pb-24">
      <AppHeader />

      <main className="container py-6 space-y-6">
        {/* ì‚¬ìš©ì ì •ë³´ */}
        <motion.div
          initial={{ opacity: 0, y: 10 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.3 }}
          className="card"
        >
          <h2 className="text-lg font-bold mb-3">í”„ë¡œí•„</h2>
          <div className="space-y-1">
            {user?.username && (
              <p className="text-text-primary font-medium">{user.username}</p>
            )}
            {user?.email && (
              <p className="text-text-secondary text-sm">{user.email}</p>
            )}
          </div>
        </motion.div>

        {/* ë‚œì´ë„ ì„¤ì • */}
        <motion.div
          initial={{ opacity: 0, y: 10 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.3, delay: 0.1 }}
          className="card"
        >
          <h2 className="text-lg font-bold mb-3">í•™ìŠµ ë‚œì´ë„</h2>
          <div className="flex gap-2">
            {DIFFICULTY_OPTIONS.map((option) => {
              const isSelected = settings.difficulty === option.value;
              return (
                <button
                  key={option.value}
                  onClick={() => setDifficulty(option.value)}
                  className={`flex-1 py-2.5 rounded-lg text-sm font-medium transition-colors ${
                    isSelected
                      ? 'bg-primary text-white'
                      : 'bg-surface border border-border text-text-secondary hover:text-text-primary'
                  }`}
                >
                  {option.label}
                </button>
              );
            })}
          </div>
        </motion.div>

        {/* ë¡œê·¸ì•„ì›ƒ ë²„íŠ¼ */}
        <motion.div
          initial={{ opacity: 0, y: 10 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.3, delay: 0.2 }}
          className="space-y-3"
        >
          <button
            onClick={() => {
              logout();
              navigate('/auth');
            }}
            className="btn-secondary w-full"
          >
            ë¡œê·¸ì•„ì›ƒ
          </button>
        </motion.div>

        {/* ë¬¸ì˜ì‚¬í•­ */}
        <motion.div
          initial={{ opacity: 0, y: 10 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.3, delay: 0.25 }}
          className="card"
        >
          <ContactSection />
        </motion.div>

        {/* ì•± ì •ë³´ */}
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.3, delay: 0.3 }}
          className="text-center pt-4"
        >
          <p className="text-text-secondary text-xs">Narrative Investment v0.1.0</p>
        </motion.div>
      </main>
    </div>
  );
}

