    69â†’function normalizeKeywords(keywords) {
    70â†’  const items = Array.isArray(keywords) ? keywords : [];
    71â†’  const normalized = items
    72â†’    .filter((item) => item && typeof item === 'object')
    73â†’    .map((item) => ({
    74â†’      title: String(item.title || '').trim(),
    75â†’      description: String(item.description || '').trim(),
    76â†’    }))
    77â†’    .filter((item) => item.title.length > 0);
    78â†’
    79â†’  const deduped = [];
    80â†’  const seen = new Set();
    81â†’  for (const item of normalized) {
    82â†’    const key = item.title.toLowerCase();
    83â†’    if (seen.has(key)) continue;
    84â†’    seen.add(key);
    85â†’    deduped.push(item);
    86â†’  }
    87â†’
    88â†’  return deduped;
    89â†’}
