    96â†’  for (const item of merged) {
    97â†’    if (seeds.length >= 3) break;
    98â†’    seeds.push(item);
    99â†’  }
   100â†’
   101â†’  while (seeds.length < 3) {
   102â†’    seeds.push(FALLBACK_KEYWORDS[seeds.length % FALLBACK_KEYWORDS.length]);
   103â†’  }
   104â†’
   105â†’  return { keywordPool: merged, seeds };
   106â†’}
