   108â†’function shortenText(text) {
   109â†’  if (!text) return '';
   110â†’  const firstSentence = text.split(/[.?!](\s|$)/)[0].trim();
   111â†’  if (firstSentence.length > 35) {
   112â†’    return firstSentence.substring(0, 32) + '...';
   113â†’  }
   114â†’  return firstSentence;
   115â†’}
