   117â†’function buildOptions(correctDescription, wrongPool, idx) {
   118â†’  const wrongs = [];
   119â†’  for (const candidate of wrongPool) {
   120â†’    const text = shortenText(candidate);
   121â†’    if (!text || text === correctDescription || wrongs.includes(text)) continue;
   122â†’    wrongs.push(text);
   123â†’    if (wrongs.length >= 3) break;
   124â†’  }
   125â†’
   126â†’  const fallbackWrongs = [
   127â†’    'ì¥ê¸°ì ì¸ ê°€ê²© ì¶”ì„¸ë¥¼ ë”°ë¼ê°€ëŠ” ì „ëµ',
   128â†’    'ê±°ë˜ëŸ‰ ê¸‰ì¦ê³¼ ë‰´ìŠ¤ ê°•ë„ë§Œ ë¶„ì„í•˜ëŠ” ê²ƒ',
   129â†’    'í‰ê·  ë‹¨ê°€ë¥¼ ë‚®ì¶”ê¸° ìœ„í•´ ê³„ì† ë§¤ìˆ˜í•˜ëŠ” ê²ƒ',
   130â†’    'ì˜¤ë¡œì§€ ì°¨íŠ¸ì˜ ê¸°ìˆ ì  ì§€í‘œë§Œ ë³´ê³  íŒë‹¨í•˜ëŠ” ê²ƒ'
   131â†’  ];
   132â†’
   133â†’  for (const fallback of fallbackWrongs) {
   134â†’    if (wrongs.length >= 3) break;
   135â†’    if (fallback !== correctDescription && !wrongs.includes(fallback)) {
   136â†’      wrongs.push(fallback);
   137â†’    }
   138â†’  }
   139â†’
   140â†’  const base = [correctDescription, ...wrongs.slice(0, 3)];
   141â†’  const rotate = idx % base.length;
   142â†’  const options = [...base.slice(rotate), ...base.slice(0, rotate)];
   143â†’  const correctAnswer = options.findIndex((option) => option === correctDescription);
   144â†’
   145â†’  return { options, correctAnswer };
   146â†’}
