   148â†’function buildDailyQuestions(keywords, dateKey) {
   149â†’  const { keywordPool, seeds } = takeQuestionSeeds(keywords);
   150â†’  const fallbackDescriptions = keywordPool.map((item) => item.description).filter(Boolean);
   151â†’
   152â†’  return seeds.map((seed, idx) => {
   153â†’    const rawDesc = seed.description || `${seed.title} ê´€ë ¨ ë³€í™”ë¥¼ ì ê²€í•˜ëŠ” ê²ƒ`;
   154â†’    const correctDescription = shortenText(rawDesc);
   155â†’    const wrongPool = fallbackDescriptions.filter((desc) => desc !== rawDesc);
   156â†’    const { options, correctAnswer } = buildOptions(correctDescription, wrongPool, idx);
   157â†’
   158â†’    return {
   159â†’      id: `q-${idx + 1}`,
   160â†’      scenarioId: `daily-${dateKey}-${idx + 1}`,
   161â†’      title: seed.title,
   162â†’      prompt: `ë‹¤ìŒ ì¤‘ "${seed.title}"ì— ëŒ€í•œ ê°€ì¥ ì ì ˆí•œ ì„¤ëª…ì€?`,
   163â†’      options,
   164â†’      correctAnswer,
   165â†’    };
   166â†’  });
   167â†’}
