   169â†’function evaluateAnswers(questions, answers) {
   170â†’  const answerMap = answers || {};
   171â†’  let score = 0;
   172â†’
   173â†’  for (const question of questions) {
   174â†’    if (answerMap[question.id] === question.correctAnswer) {
   175â†’      score += 1;
   176â†’    }
   177â†’  }
   178â†’
   179â†’  return {
   180â†’    score,
   181â†’    total: questions.length,
   182â†’  };
   183â†’}
