   185â†’export default function useDailyQuiz(keywords) {
   186â†’  const dateKey = getKstTodayDateKey();
   187â†’  const questions = useMemo(() => buildDailyQuestions(keywords, dateKey), [keywords, dateKey]);
   188â†’  const [dailyState, setDailyState] = useState(() => readDailyState(dateKey));
   189â†’
   190â†’  useEffect(() => {
   191â†’    setDailyState(readDailyState(dateKey));
   192â†’  }, [dateKey]);
   193â†’
   194â†’  const persistState = useCallback((nextState) => {
   195â†’    writeDailyState(nextState);
   196â†’    setDailyState(nextState);
   197â†’  }, []);
   198â†’
   199â†’  const settleReward = useCallback(async (answers, onlyUnpaid = false) => {
   200â†’    const current = readDailyState(dateKey);
   201â†’    const paidSet = new Set(current.paidQuestionIds || []);
   202â†’    const targets = questions.filter((question) => !paidSet.has(question.id));
   203â†’    const evaluation = evaluateAnswers(questions, answers);
   204â†’
   205â†’    if (onlyUnpaid && targets.length === 0) {
   206â†’      return {
   207â†’        mode: 'reward',
   208â†’        ...evaluation,
   209â†’        rewardTotal: 0,
   210â†’        failedQuestionIds: [],
   211â†’        paidQuestionIds: current.paidQuestionIds,
   212â†’        remainingRewardQuestions: 0,
   213â†’      };
   214â†’    }
   215â†’
   216â†’    let rewardTotal = 0;
   217â†’    const failedQuestionIds = [];
   218â†’    const errorMessages = [];
   219â†’
   220â†’    for (const question of targets) {
   221â†’      const selectedAnswer = Number.isInteger(answers?.[question.id]) ? answers[question.id] : -1;
   222â†’
   223â†’      try {
   224â†’        const response = await quizApi.claimReward({
   225â†’          scenario_id: question.scenarioId,
   226â†’          selected_answer: selectedAnswer,
   227â†’          correct_answer: question.correctAnswer,
   228â†’        });
   229â†’
   230â†’        rewardTotal += Number(response?.reward_amount || 0);
   231â†’        paidSet.add(question.id);
   232â†’      } catch (error) {
   233â†’        failedQuestionIds.push(question.id);
   234â†’        errorMessages.push(error?.message || `${question.id} ë³´ìƒ ì§€ê¸‰ì— ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤.`);
   235â†’      }
   236â†’    }
   237â†’
   238â†’    const nextState = {
   239â†’      dateKey,
   240â†’      rewardAttempted: true,
   241â†’      paidQuestionIds: Array.from(paidSet),
   242â†’      lastScore: evaluation.score,
   243â†’      lastRewardTotal: rewardTotal,
   244â†’      lastAttemptAt: new Date().toISOString(),
   245â†’    };
   246â†’
   247â†’    persistState(nextState);
   248â†’
   249â†’    return {
   250â†’      mode: 'reward',
   251â†’      ...evaluation,
   252â†’      rewardTotal,
   253â†’      failedQuestionIds,
   254â†’      errorMessages,
   255â†’      paidQuestionIds: nextState.paidQuestionIds,
   256â†’      remainingRewardQuestions: questions.length - nextState.paidQuestionIds.length,
   257â†’    };
   258â†’  }, [dateKey, persistState, questions]);
   259â†’
   260â†’  const submitQuiz = useCallback(async (answers) => {
   261â†’    const current = readDailyState(dateKey);
   262â†’    const evaluation = evaluateAnswers(questions, answers);
   263â†’
   264â†’    if (current.rewardAttempted) {
   265â†’      const nextState = {
   266â†’        ...current,
   267â†’        lastScore: evaluation.score,
   268â†’        lastRewardTotal: 0,
   269â†’        lastAttemptAt: new Date().toISOString(),
   270â†’      };
   271â†’      persistState(nextState);
   272â†’
   273â†’      return {
   274â†’        mode: 'practice',
   275â†’        ...evaluation,
   276â†’        rewardTotal: 0,
   277â†’        failedQuestionIds: [],
   278â†’        paidQuestionIds: nextState.paidQuestionIds,
   279â†’        remainingRewardQuestions: questions.length - nextState.paidQuestionIds.length,
   280â†’      };
   281â†’    }
   282â†’
   283â†’    return settleReward(answers, false);
   284â†’  }, [dateKey, persistState, questions, settleReward]);
   285â†’
   286â†’  const retryUnpaidRewards = useCallback(async (answers) => {
   287â†’    const current = readDailyState(dateKey);
   288â†’    if (!current.rewardAttempted) {
   289â†’      return submitQuiz(answers);
   290â†’    }
   291â†’
   292â†’    if ((current.paidQuestionIds || []).length >= questions.length) {
   293â†’      return {
   294â†’        mode: 'reward',
   295â†’        ...evaluateAnswers(questions, answers),
   296â†’        rewardTotal: 0,
   297â†’        failedQuestionIds: [],
   298â†’        paidQuestionIds: current.paidQuestionIds,
   299â†’        remainingRewardQuestions: 0,
   300â†’      };
   301â†’    }
   302â†’
   303â†’    return settleReward(answers, true);
   304â†’  }, [dateKey, questions, settleReward, submitQuiz]);
   305â†’
   306â†’  const paidCount = dailyState.paidQuestionIds.length;
   307â†’  const totalQuestions = questions.length;
   308â†’  const hasPendingRewards = dailyState.rewardAttempted && paidCount < totalQuestions;
   309â†’  const hasClaimedAllRewards = dailyState.rewardAttempted && paidCount >= totalQuestions;
   310â†’
   311â†’  return {
   312â†’    dateKey,
   313â†’    questions,
   314â†’    dailyState,
   315â†’    paidCount,
   316â†’    totalQuestions,
   317â†’    hasPendingRewards,
   318â†’    hasClaimedAllRewards,
   319â†’    submitQuiz,
   320â†’    retryUnpaidRewards,
   321â†’  };
   322â†’}
