303→  }, [sendMessage]);
304→
305→  const clearMessages = useCallback(() => {
306→    setMessages([]);
307→    setSessionId(null);
308→    setCurrentTerm(null);
309→    setLastFailedMessage(null);
310→    setLastFailedDifficulty(null);
311→    retryCountRef.current = 0;
312→  }, []);
313→
314→  const value = useMemo(() => ({
315→    isOpen,
316→    openTutor,
317→    closeTutor,
318→    messages,
319→    isLoading,
320→    sendMessage,
321→    clearMessages,
322→    contextInfo,
323→    setContextInfo,
324→    currentTerm,
325→    setCurrentTerm,
326→    sessions,
327→    activeSessionId,
328→    createNewChat,
329→    deleteChat,
330→    loadChatHistory,
331→    requestVisualization,
332→    retryLastMessage,
333→    lastFailedMessage,
334→  }), [
335→    isOpen, openTutor, closeTutor, messages, isLoading, sendMessage,
336→    clearMessages, contextInfo, currentTerm, sessions, activeSessionId,
337→    createNewChat, deleteChat, loadChatHistory, requestVisualization,
338→    retryLastMessage, lastFailedMessage,
339→  ]);
340→
341→  return (
342→    <TutorContext.Provider value={value}>
343→      {children}
344→    </TutorContext.Provider>
345→  );
346→}
347→
348→export function useTutor() {
349→  const context = useContext(TutorContext);
350→  if (!context) throw new Error('useTutor must be used within a TutorProvider');
351→  return context;
